import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const CoupleProfileSetup = () => {
  const navigate = useNavigate();

  const [coupleData, setCoupleData] = useState({
    partner1Name: "",
    partner2Name: "",
    weddingDate: "",
    contactNumber: "",
    email: "",
    location: "",
    serviceDescription: "",
    user_id: "",
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  // ‚úÖ Load user_id from localStorage once
  useEffect(() => {
    const storedUserId = localStorage.getItem("user_id");
    if (storedUserId) {
      setCoupleData((prev) => ({ ...prev, user_id: storedUserId }));
    }
  }, []);

  // ‚úÖ Fetch Couple Profile Data
  useEffect(() => {
    if (!coupleData.user_id) return;

    const fetchProfile = async () => {
      try {
        const response = await fetch(`http://localhost:3000/api/couple/profile/${coupleData.user_id}`);
        const result = await response.json();

        if (result.status === "success") {
          setCoupleData((prev) => ({
            ...prev,
            partner1Name: result.data.partner1Name || "",
            partner2Name: result.data.partner2Name || "",
            weddingDate: result.data.weddingDate || "",
            contactNumber: result.data.contactNumber || "",
            email: result.data.email || "",
            location: result.data.location || "",
            serviceDescription: result.data.serviceDescription || "",
          }));
        }
      } catch (error) {
        console.error("Error fetching couple profile:", error);
      }
    };

    fetchProfile();
  }, [coupleData.user_id]);

  // ‚úÖ Handle input change
  const handleChange = (e) => {
    setCoupleData({ ...coupleData, [e.target.name]: e.target.value });
  };

  // ‚úÖ Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    if (!coupleData.user_id) {
      setMessage("‚ùå User ID not found. Please log in again.");
      setLoading(false);
      return;
    }

    const formData = new FormData();
    formData.append("user_id", coupleData.user_id);
    formData.append("partner1Name", coupleData.partner1Name);
    formData.append("partner2Name", coupleData.partner2Name);
    formData.append("weddingDate", coupleData.weddingDate);
    formData.append("contactNumber", coupleData.contactNumber);
    formData.append("location", coupleData.location);
    formData.append("serviceDescription", coupleData.serviceDescription);

    try {
      const response = await fetch("http://localhost:3000/api/couple/profile", {
        method: "PUT",
        body: formData,
      });

      const result = await response.json();

      if (response.ok && result.status === "success") {
        setMessage("‚úÖ Profile updated successfully!");
        setTimeout(() => navigate("/couple-dashboard"), 1000);
      } else {
        setMessage(result.message || "‚ùå Something went wrong!");
      }
    } catch (error) {
      console.error("‚ùå Error updating profile:", error);
      setMessage("‚ö†Ô∏è Server error, please try again later.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-pink-200 to-purple-300 p-6">
      <div className="bg-white p-8 rounded-2xl shadow-2xl w-full max-w-2xl border border-gray-200">
        <h2 className="text-center text-4xl font-bold text-white mb-6 bg-gradient-to-r from-pink-500 to-purple-400 py-4 rounded-lg shadow-md">
          üíç Couple Profile Setup
        </h2>
        
        <p className="text-center text-gray-700 mb-6">
          Please fill out the form below to set up your couple profile. Ensure all details are accurate and up-to-date.
        </p>

        <form onSubmit={handleSubmit}>
          <div className="flex gap-8">
            {/* Left Section - Inputs */}
            <div className="w-1/2 flex flex-col space-y-4">
              <input type="text" name="partner1Name" placeholder="Partner 1 Name" value={coupleData.partner1Name} onChange={handleChange} className="w-full p-3 border rounded-lg bg-gray-100 shadow-sm" required />
              <input type="text" name="partner2Name" placeholder="Partner 2 Name" value={coupleData.partner2Name} onChange={handleChange} className="w-full p-3 border rounded-lg bg-gray-100 shadow-sm" required />
              <input type="date" name="weddingDate" placeholder="Wedding Date" value={coupleData.weddingDate} onChange={handleChange} className="w-full p-3 border rounded-lg bg-gray-100 shadow-sm" required />
              <input type="tel" name="contactNumber" placeholder="Contact Number" value={coupleData.contactNumber} onChange={handleChange} className="w-full p-3 border rounded-lg bg-gray-100 shadow-sm" required />
              <input type="text" name="location" placeholder="Location" value={coupleData.location} onChange={handleChange} className="w-full p-3 border rounded-lg bg-gray-100 shadow-sm" required />
              <textarea name="serviceDescription" placeholder="Service Description" value={coupleData.serviceDescription} onChange={handleChange} className="w-full p-3 border rounded-lg bg-gray-100 shadow-sm" />
            </div>
          </div>

          <button type="submit" className="w-full bg-purple-500 text-white py-3 rounded-lg mt-6 hover:bg-purple-600 transition">
            {loading ? "üöÄ Saving..." : "üíæ Save Profile"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default CoupleProfileSetup;
