import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const ProfileSetup = () => {
  const [description, setDescription] = useState("");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfileData = async () => {
      const authToken = localStorage.getItem("authToken");
      const user_id = localStorage.getItem("user_id");

      if (!authToken || !user_id) {
        navigate("/login");
        return;
      }

      try {
        const response = await fetch(`http://localhost:3000/api/profile/${user_id}`, {
          headers: { Authorization: `Bearer ${authToken}` },
        });
        const data = await response.json();

        if (response.ok && data.status === "success") {
          setDescription(data.data.description || "");
        } else if (response.status === 401) {
          // Unauthorized: Redirect to login
          navigate("/login");
        } else {
          console.error("Failed to fetch profile data:", data.message);
        }
      } catch (error) {
        console.error("Error fetching profile data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchProfileData();
  }, [navigate]);

  const handleSave = async (e) => {
    e.preventDefault();
    // ...existing code for saving profile data...
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen text-lg">
        <div className="animate-spin rounded-full h-12 w-12 border-t-4 border-pink-500"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen p-6">
      <form onSubmit={handleSave} className="max-w-lg mx-auto bg-white p-6 rounded-lg shadow-lg">
        <div className="mb-4">
          <label htmlFor="description" className="block text-gray-700 font-semibold">
            Description
          </label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-pink-400"
          />
        </div>
        <button type="submit" className="w-full bg-pink-500 text-white py-2 rounded-lg hover:bg-pink-600">
          Save
        </button>
      </form>
    </div>
  );
};

export default ProfileSetup;
